use crate::Solution;

#[test]
// #[ignore]
fn case_1() {
    assert!(
        Solution::longest_palindrome(("babad").to_string()) == "bab" ||
        Solution::longest_palindrome(("babad").to_string()) == "aba"
    );
}

#[test]
// #[ignore]
fn case_2() {
    assert_eq!(
        Solution::longest_palindrome(("cbbd").to_string()),
        "bb"
    );
}

#[test]
// #[ignore]
fn case_3() {
    assert_eq!(
        Solution::longest_palindrome(("").to_string()),
        "khvhk"
    );   
}